// frontend/src/components/Navbar.jsx
// Main navigation component with Clerk integration

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { SignedIn, SignedOut, UserButton, useUser } from '@clerk/clerk-react';
import { Menu, X, ChevronDown, Video, Users, Briefcase, Phone } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [coursesOpen, setCoursesOpen] = useState(false);
  const [liveOpen, setLiveOpen] = useState(false);
  const [investmentOpen, setInvestmentOpen] = useState(false);
  const navigate = useNavigate();
  const { user } = useUser();

  const isAdmin = user?.primaryEmailAddress?.emailAddress === 'ashubitmesra121@gmail.com';

  // Courses dropdown - LEFT SIDE (Trending Courses)
  const trendingCourses = [
    { name: 'Passive Income through Options Selling', path: '/courses' },
    { name: 'Price Action Strategy using CPR', path: '/courses' },
    { name: 'Intraday Trading Strategies', path: '/courses' },
    { name: 'Momentum Value Strategy', path: '/courses' },
    { name: 'Trading Secrets', path: '/courses' },
  ];

  // Courses dropdown - RIGHT SIDE (Top Categories)
  const topCategories = [
    { name: 'Option Trading', path: '/courses' },
    { name: 'Technical Analysis', path: '/courses' },
    { name: 'Trading Strategies', path: '/courses' },
    { name: 'Stock Market Investing', path: '/courses' },
  ];

  // Live dropdown items
  const liveItems = [
    {
      title: 'Webinars',
      description: 'Stay updated with the latest trading and investing strategies through our expert-led live webinars.',
      features: ['Real-time market discussions', 'Interactive Q&A sessions', 'Access recordings anytime'],
      icon: Video,
      link: '/live/webinars',
      color: 'text-blue-400'
    },
    {
      title: 'Mentorship Programs',
      description: 'Join structured mentorship programs led by SEBI-certified mentors to build strong trading skills.',
      features: ['Step-by-step learning path', 'Small group mentorship', 'Weekly live sessions'],
      icon: Users,
      link: '/live/mentorship',
      color: 'text-green-400'
    },
    {
      title: 'Workshops',
      description: 'Hands-on stock market & trading workshops in both online and offline modes.',
      features: ['Practical learning with real-time examples', 'Short, focused learning modules', 'Offline sessions in major cities'],
      icon: Briefcase,
      link: '/live/workshops',
      color: 'text-purple-400'
    },
    {
      title: '1:1 Guidance',
      description: 'Get personalized trading guidance from industry experts, tailored just for you.',
      features: ['One-on-one private sessions', 'Customized trading roadmap', 'Solve your specific doubts'],
      icon: Phone,
      link: '/live/guidance',
      color: 'text-yellow-400'
    }
  ];

  // Investment dropdown items
  const investmentDropdown = [
    { name: 'Mutual Funds', path: '/investment/mutual-funds' },
    { name: 'Digital Gold', path: '/investment/digital-gold' },
    { name: 'Fixed Deposits (FD)', path: '/investment/fixed-deposits' },
    { name: 'Insurance', path: '/investment/insurance' },
    { name: 'AI Calculator', path: '/investment/ai-calculator' },
  ];

  return (
    <nav className="fixed top-0 w-full bg-black/90 backdrop-blur-md z-50 border-b border-zinc-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-2">
            <div className="w-10 h-10 bg-gradient-to-br from-yellow-400 to-yellow-600 rounded-lg flex items-center justify-center">
              <span className="text-black font-bold text-xl">iS</span>
            </div>
            <span className="text-xl font-bold text-white">iStockly</span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden lg:flex items-center space-x-8">
            {/* Home */}
            <Link to="/" className="text-gray-300 hover:text-yellow-400 transition-colors">
              Home
            </Link>

            {/* Courses Dropdown */}
            <div className="relative group">
              <button
                onMouseEnter={() => setCoursesOpen(true)}
                onMouseLeave={() => setCoursesOpen(false)}
                onClick={() => navigate('/courses')}
                className="flex items-center space-x-1 text-gray-300 hover:text-yellow-400 transition-colors"
              >
                <span>Courses</span>
                <ChevronDown className="w-4 h-4" />
              </button>
              <AnimatePresence>
                {coursesOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    onMouseEnter={() => setCoursesOpen(true)}
                    onMouseLeave={() => setCoursesOpen(false)}
                    className="absolute top-full left-0 mt-2 w-[600px] bg-zinc-900 border border-zinc-800 rounded-lg shadow-xl py-4"
                  >
                    <div className="grid grid-cols-2 gap-6 px-6">
                      {/* Left Column - Trending Courses */}
                      <div>
                        <h3 className="text-sm font-semibold text-gray-400 mb-3 uppercase">
                          Trending Courses
                        </h3>
                        <div className="space-y-2">
                          {trendingCourses.map((item) => (
                            <Link
                              key={item.name}
                              to={item.path}
                              className="block text-gray-300 hover:text-yellow-400 transition-colors text-sm"
                            >
                              {item.name}
                            </Link>
                          ))}
                          <Link
                            to="/courses"
                            className="block text-yellow-400 hover:text-yellow-500 transition-colors text-sm font-medium mt-4"
                          >
                            View All Courses →
                          </Link>
                        </div>
                      </div>

                      {/* Right Column - Top Categories */}
                      <div>
                        <h3 className="text-sm font-semibold text-gray-400 mb-3 uppercase">
                          Top Categories
                        </h3>
                        <div className="space-y-2">
                          {topCategories.map((item) => (
                            <Link
                              key={item.name}
                              to={item.path}
                              className="flex items-center space-x-2 text-gray-300 hover:text-yellow-400 transition-colors text-sm"
                            >
                              <span className="w-2 h-2 bg-yellow-400 rounded-full"></span>
                              <span>{item.name}</span>
                            </Link>
                          ))}
                        </div>
                      </div>
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Live Dropdown */}
            <div className="relative group">
              <button
                onMouseEnter={() => setLiveOpen(true)}
                onMouseLeave={() => setLiveOpen(false)}
                className="flex items-center space-x-1 text-gray-300 hover:text-yellow-400 transition-colors"
              >
                <span>Live</span>
                <ChevronDown className="w-4 h-4" />
              </button>
              <AnimatePresence>
                {liveOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    onMouseEnter={() => setLiveOpen(true)}
                    onMouseLeave={() => setLiveOpen(false)}
                    className="absolute top-full right-0 mt-2 w-[800px] bg-zinc-900 border border-zinc-800 rounded-lg shadow-xl p-6"
                  >
                    <div className="grid grid-cols-2 gap-4">
                      {liveItems.map((item, index) => {
                        const Icon = item.icon;
                        return (
                          <Link
                            key={index}
                            to={item.link}
                            className="block p-4 rounded-lg hover:bg-zinc-800 transition-colors group"
                          >
                            <div className="flex items-start space-x-3">
                              <Icon className={`w-6 h-6 ${item.color} flex-shrink-0 mt-1`} />
                              <div className="flex-1">
                                <h3 className="text-white font-semibold mb-1 group-hover:text-yellow-400 transition-colors">
                                  {item.title}
                                </h3>
                                <p className="text-gray-400 text-sm mb-2 line-clamp-2">
                                  {item.description}
                                </p>
                                <ul className="space-y-1">
                                  {item.features.map((feature, idx) => (
                                    <li key={idx} className="text-gray-500 text-xs flex items-center">
                                      <span className="text-green-400 mr-1">✓</span>
                                      {feature}
                                    </li>
                                  ))}
                                </ul>
                              </div>
                            </div>
                          </Link>
                        );
                      })}
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Investment Dropdown */}
            <div className="relative group">
              <button
                onMouseEnter={() => setInvestmentOpen(true)}
                onMouseLeave={() => setInvestmentOpen(false)}
                onClick={() => navigate('/investment')}
                className="flex items-center space-x-1 text-gray-300 hover:text-yellow-400 transition-colors"
              >
                <span>Investment</span>
                <ChevronDown className="w-4 h-4" />
              </button>
              <AnimatePresence>
                {investmentOpen && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: 10 }}
                    onMouseEnter={() => setInvestmentOpen(true)}
                    onMouseLeave={() => setInvestmentOpen(false)}
                    className="absolute top-full left-0 mt-2 w-56 bg-zinc-900 border border-zinc-800 rounded-lg shadow-xl py-4"
                  >
                    <div className="px-2">
                      {investmentDropdown.map((item) => (
                        <Link
                          key={item.name}
                          to={item.path}
                          className="block px-4 py-2 text-gray-300 hover:text-yellow-400 hover:bg-zinc-800 rounded transition-colors"
                        >
                          {item.name}
                        </Link>
                      ))}
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Blog */}
            <Link to="/blog" className="text-gray-300 hover:text-yellow-400 transition-colors">
              Blog
            </Link>
          </div>

          {/* Auth Buttons */}
          <div className="hidden lg:flex items-center space-x-4">
            <SignedOut>
              <Link
                to="/sign-in"
                className="text-gray-300 hover:text-yellow-400 transition-colors"
              >
                Sign In
              </Link>
              <Link
                to="/sign-up"
                className="px-4 py-2 bg-yellow-500 text-black rounded-lg hover:bg-yellow-600 transition-colors font-medium"
              >
                Sign Up
              </Link>
            </SignedOut>
            <SignedIn>
              <Link
                to="/dashboard"
                className="text-gray-300 hover:text-yellow-400 transition-colors"
              >
                Dashboard
              </Link>
              {isAdmin && (
                <Link
                  to="/admin"
                  className="text-gray-300 hover:text-yellow-400 transition-colors"
                >
                  Admin
                </Link>
              )}
              <UserButton afterSignOutUrl="/" />
            </SignedIn>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsOpen(!isOpen)}
            className="lg:hidden text-white"
          >
            {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            className="lg:hidden bg-zinc-900 border-t border-zinc-800"
          >
            <div className="px-4 py-6 space-y-4">
              {/* Home */}
              <Link
                to="/"
                onClick={() => setIsOpen(false)}
                className="block text-gray-300 hover:text-yellow-400 transition-colors font-medium"
              >
                Home
              </Link>

              {/* Courses */}
              <div>
                <Link
                  to="/courses"
                  onClick={() => setIsOpen(false)}
                  className="block text-gray-300 hover:text-yellow-400 transition-colors font-medium"
                >
                  Courses
                </Link>
                <div className="ml-4 mt-2 space-y-3">
                  <div className="text-xs font-semibold text-gray-500 uppercase">
                    Trending Courses
                  </div>
                  {trendingCourses.map((item) => (
                    <Link
                      key={item.name}
                      to={item.path}
                      onClick={() => setIsOpen(false)}
                      className="block text-sm text-gray-400 hover:text-yellow-400 transition-colors"
                    >
                      {item.name}
                    </Link>
                  ))}
                  <div className="text-xs font-semibold text-gray-500 uppercase mt-4">
                    Top Categories
                  </div>
                  {topCategories.map((item) => (
                    <Link
                      key={item.name}
                      to={item.path}
                      onClick={() => setIsOpen(false)}
                      className="block text-sm text-gray-400 hover:text-yellow-400 transition-colors"
                    >
                      {item.name}
                    </Link>
                  ))}
                </div>
              </div>

              {/* Live Programs */}
              <div>
                <div className="text-gray-300 font-medium mb-2">Live Programs</div>
                <div className="ml-4 space-y-2">
                  {liveItems.map((item, index) => (
                    <Link
                      key={index}
                      to={item.link}
                      onClick={() => setIsOpen(false)}
                      className="block text-sm text-gray-400 hover:text-yellow-400 transition-colors"
                    >
                      {item.title}
                    </Link>
                  ))}
                </div>
              </div>

              {/* Investment */}
              <div>
                <Link
                  to="/investment"
                  onClick={() => setIsOpen(false)}
                  className="block text-gray-300 hover:text-yellow-400 transition-colors font-medium"
                >
                  Investment
                </Link>
                <div className="ml-4 mt-2 space-y-2">
                  {investmentDropdown.map((item) => (
                    <Link
                      key={item.name}
                      to={item.path}
                      onClick={() => setIsOpen(false)}
                      className="block text-sm text-gray-400 hover:text-yellow-400 transition-colors"
                    >
                      {item.name}
                    </Link>
                  ))}
                </div>
              </div>

              {/* Blog */}
              <Link
                to="/blog"
                onClick={() => setIsOpen(false)}
                className="block text-gray-300 hover:text-yellow-400 transition-colors font-medium"
              >
                Blog
              </Link>

              {/* Auth */}
              <SignedOut>
                <Link
                  to="/sign-in"
                  onClick={() => setIsOpen(false)}
                  className="block text-gray-300 hover:text-yellow-400 transition-colors"
                >
                  Sign In
                </Link>
                <Link
                  to="/sign-up"
                  onClick={() => setIsOpen(false)}
                  className="block px-4 py-2 bg-yellow-500 text-black rounded-lg hover:bg-yellow-600 transition-colors font-medium text-center"
                >
                  Sign Up
                </Link>
              </SignedOut>
              <SignedIn>
                <Link
                  to="/dashboard"
                  onClick={() => setIsOpen(false)}
                  className="block text-gray-300 hover:text-yellow-400 transition-colors"
                >
                  Dashboard
                </Link>
                {isAdmin && (
                  <Link
                    to="/admin"
                    onClick={() => setIsOpen(false)}
                    className="block text-gray-300 hover:text-yellow-400 transition-colors"
                  >
                    Admin
                  </Link>
                )}
              </SignedIn>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
};

export default Navbar;
