// frontend/src/components/PhoneNumberCollector.jsx
// Component to collect phone number after Clerk authentication

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Phone, AlertCircle, CheckCircle } from 'lucide-react';
import axios from 'axios';

const PhoneNumberCollector = ({ user, onComplete }) => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isValid, setIsValid] = useState(false);

  const validatePhone = (phone) => {
    // Indian phone number validation: 10 digits starting with 6-9
    const phoneRegex = /^[6-9]\d{9}$/;
    const cleaned = phone.replace(/\D/g, ''); // Remove non-digits
    
    if (cleaned.length === 10 && phoneRegex.test(cleaned)) {
      setIsValid(true);
      setError('');
      return cleaned;
    } else if (cleaned.length > 0) {
      setIsValid(false);
      setError('Please enter a valid 10-digit Indian mobile number');
      return null;
    } else {
      setIsValid(false);
      setError('');
      return null;
    }
  };

  const handlePhoneChange = (e) => {
    const value = e.target.value;
    setPhoneNumber(value);
    validatePhone(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    const cleanedPhone = validatePhone(phoneNumber);
    if (!cleanedPhone) {
      setError('Please enter a valid 10-digit mobile number');
      return;
    }

    if (!user) {
      setError('User information not available');
      return;
    }

    try {
      setLoading(true);
      const token = await user.getToken();
      
      await axios.post(
        `${process.env.REACT_APP_API_URL}/user/sync`,
        {
          phoneNumber: cleanedPhone,
          email: user.primaryEmailAddress?.emailAddress,
          firstName: user.firstName,
          lastName: user.lastName
        },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      
      onComplete();
    } catch (error) {
      console.error('Error syncing user:', error);
      setError(error.response?.data?.error || 'Failed to save phone number. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-black flex items-center justify-center pt-16 px-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-md"
      >
        <div className="bg-zinc-900 border border-zinc-800 rounded-2xl p-8 shadow-2xl">
          <div className="text-center mb-8">
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2, type: "spring" }}
              className="w-16 h-16 bg-yellow-400/10 rounded-full flex items-center justify-center mx-auto mb-4"
            >
              <Phone className="w-8 h-8 text-yellow-400" />
            </motion.div>
            <h2 className="text-2xl font-bold text-white mb-2">Almost There!</h2>
            <p className="text-gray-400">Please provide your mobile number to continue</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Mobile Number <span className="text-red-400">*</span>
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                  <span className="text-gray-400 text-sm">+91</span>
                </div>
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={handlePhoneChange}
                  placeholder="Enter your 10-digit mobile number"
                  maxLength={10}
                  className={`w-full pl-14 pr-4 py-3 bg-zinc-800 border rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 transition-all ${
                    error
                      ? 'border-red-500 focus:ring-red-500/50'
                      : isValid
                      ? 'border-green-500 focus:ring-green-500/50'
                      : 'border-zinc-700 focus:border-yellow-400 focus:ring-yellow-400/50'
                  }`}
                  required
                  autoFocus
                />
                {isValid && (
                  <div className="absolute inset-y-0 right-0 pr-4 flex items-center">
                    <CheckCircle className="w-5 h-5 text-green-400" />
                  </div>
                )}
              </div>
              {error && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="mt-2 flex items-center gap-2 text-red-400 text-sm"
                >
                  <AlertCircle className="w-4 h-4" />
                  <span>{error}</span>
                </motion.div>
              )}
              {!error && phoneNumber && !isValid && (
                <p className="mt-2 text-gray-500 text-xs">
                  Enter a valid 10-digit Indian mobile number
                </p>
              )}
            </div>

            <motion.button
              type="submit"
              disabled={loading || !isValid}
              whileHover={{ scale: loading || !isValid ? 1 : 1.02 }}
              whileTap={{ scale: loading || !isValid ? 1 : 0.98 }}
              className={`w-full py-3 rounded-lg font-semibold transition-all ${
                loading || !isValid
                  ? 'bg-gray-700 text-gray-400 cursor-not-allowed'
                  : 'bg-yellow-400 text-black hover:bg-yellow-300 shadow-lg hover:shadow-yellow-400/50'
              }`}
            >
              {loading ? (
                <span className="flex items-center justify-center gap-2">
                  <div className="w-4 h-4 border-2 border-black border-t-transparent rounded-full animate-spin" />
                  Saving...
                </span>
              ) : (
                'Continue'
              )}
            </motion.button>
          </form>

          <p className="mt-6 text-xs text-gray-500 text-center">
            Your phone number will be used for account verification and important updates
          </p>
        </div>
      </motion.div>
    </div>
  );
};

export default PhoneNumberCollector;
