// frontend/src/components/Features.jsx
// Features section component

import React from 'react';
import { motion } from 'framer-motion';
import { BookOpen, Users, TrendingUp, Award, Video, Clock } from 'lucide-react';

const Features = () => {
  const features = [
    {
      icon: BookOpen,
      title: 'Comprehensive Courses',
      description: 'Learn from beginner to advanced trading strategies with structured curriculum.',
    },
    {
      icon: Users,
      title: 'Expert Mentorship',
      description: 'Get guidance from experienced traders with proven track records.',
    },
    {
      icon: TrendingUp,
      title: 'Live Market Analysis',
      description: 'Real-time market analysis and trading insights every day.',
    },
    {
      icon: Award,
      title: 'Certification',
      description: 'Earn certificates upon course completion to showcase your skills.',
    },
    {
      icon: Video,
      title: 'Video Learning',
      description: 'High-quality video content accessible anytime, anywhere.',
    },
    {
      icon: Clock,
      title: 'Lifetime Access',
      description: 'Get unlimited access to course materials even after completion.',
    },
  ];

  return (
    <section className="py-20 bg-zinc-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-white mb-4">
            Why Choose <span className="text-yellow-400">iStockly</span>?
          </h2>
          <p className="text-gray-400 text-lg max-w-2xl mx-auto">
            We provide everything you need to become a successful trader
          </p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ y: -5 }}
              className="bg-black border border-zinc-800 rounded-xl p-6 hover:border-yellow-500/50 transition-all duration-300"
            >
              <div className="w-12 h-12 bg-yellow-500/10 rounded-lg flex items-center justify-center mb-4">
                <feature.icon className="w-6 h-6 text-yellow-400" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-2">
                {feature.title}
              </h3>
              <p className="text-gray-400">
                {feature.description}
              </p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
